
public class TextLab03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("TextLab02, 80 Point Version\n");
		
		int a = 10000;
		int b = a/3600;
		int c = 10000%3600;
		int d = c/60;
		int e = c%60;
		
		System.out.println("Starting seconds: " + a);
		System.out.println("Hours: " + b);
		System.out.println("Minutes: " + d);
		System.out.println("Seconds: " + e);
		
		
	}

}
